import React from 'react';
import './FeaturedMovie.css';

 export default({item}) => {
  
  console.log(item)
  
  return(
    <section className="featured" style={{
      background:'cover',
      backgroundPosition: 'center',
      backgroundImage: `url(https://image.tmdb.org/t/p/original${item.backdrop_path})`
    }}>
      <div className="featuredVerticalFade">
        <div className="featuredHorizontalFade"></div>
        <div className="featuredName">{item.original_name}</div>
        <div className="featuredInfo">
          <div className="featuredPoints">{item.vote_avarege} Pontos</div>
          <div className="featuredYear">2099</div>
          <div className="featuredSeason">{item.number_of_seasons} Temporada{item.number !== 1 ? 's' : ''}</div>
          <div className="featuredDescription">{item.overview}</div>
        </div>
      </div>
    </section>
  );
 }
