import React, { useEffect, useState } from 'react';
import './App.css';
import Tmdb from './Tmdb';
import MovieRow from './components/MovieRow';
import FeaturedMovie from './components/FeaturedMovie.js';

export default () => {

  const [movieList, setMovieList] = useState([]);
  const [featuredData, setFeaturedData] = useState(null);

  // Essa função tem como objetivo executar o codigo dentro de seu escopo assim que a pagina é carregada.
  useEffect(() => {
    const loadAll = async () => {

      // Pegando a lista com todos os filmes
      let list = await Tmdb.getHomeList();
      setMovieList(list);

      //Pegando o Featured
      let originals = list.filter(i => i.slug == 'originals');
      let randomChosen = Math.floor(Math.random() * (originals[0].items.results.lenght - 1));
      // let chosen = originals[0].items.results[randomChosen];

      console.log(originals[0].items.results.lenght);
    }

    loadAll();
  }, []);

  return (
    <div className="page">

      {featuredData && <FeaturedMovie item={featuredData} />}

      <section className="lists">
        {movieList.map((item, key) => (
          <MovieRow key={key} title={item.title} items={item.items} />
        ))}
      </section>
      {/* 
      Header
      Filme Destaque
      Listas  
      Rodapé 
      */}
    </div>
  );
}
